// src/components/Dashboard.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Dashboard.css'; // Add appropriate styles for the dashboard layout

const Dashboard = () => {
  const [activePage, setActivePage] = useState("allProducts"); // Set default active page

  return (
    <div className="dashboard">
      {/* Sidebar with links to different pages */}
      <div className="sidebar">
        <h2>Admin Dashboard</h2>
        <ul>
          <li>
            <Link to="/dashboard/products" onClick={() => setActivePage("allProducts")}>All Products</Link>
          </li>
          <li>
            <Link to="/dashboard/orders" onClick={() => setActivePage("orders")}>Orders</Link>
          </li>
          <li>
            <Link to="/dashboard/sales" onClick={() => setActivePage("sales")}>Sales</Link>
          </li>
          <li>
            <Link to="/dashboard/notifications" onClick={() => setActivePage("notifications")}>Notifications</Link>
          </li>
        </ul>
      </div>

      {/* Main Content for the Active Page */}
      <div className="dashboard-content">
        {activePage === "allProducts" && <AllProducts />}
        {activePage === "orders" && <Orders />}
        {activePage === "sales" && <Sales />}
        {activePage === "notifications" && <Notifications />}
      </div>
    </div>
  );
};

export default Dashboard;
