import React from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import styles from './ProductCard.module.css';

function ProductCard({ id, image, title, size, price, onAddToCart }) {
  const navigate = useNavigate(); // React Router hook

  const handleViewItem = () => {
    navigate(`/product/${id}`); // Navigate to product details page
  };

  return (
    <div className={styles.productCard}>
      <img loading="lazy" src={image} alt={title} className={styles.productImage} />
      <div className={styles.productDetails}>
        <h3 className={styles.productTitle}>{title}</h3>
        <div className={styles.productSize}>{size}</div>
        <div className={styles.productPrice}>Price: {price} KES</div>
        <div className={styles.productActions}>
          <button 
            className={styles.actionButton} 
            onClick={() => onAddToCart(id)}
          >
            Add to Cart
          </button>
          <button 
            className={styles.actionButton} 
            onClick={handleViewItem}
          >
            View Item
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProductCard;
