import React from "react";
import { Input } from "./components/Input";
import { Button } from "./components/Button";
import styles from "./LoginPage.module.css";
import { SignupForm } from "../SignupPage/SignupForm"; 

{/* <img
            loading="lazy"
            src="https://cdn.builder.io/api/v1/image/assets/TEMP/0b67fa7e4731d9d9f1c23012df146ac9df4341192b20bc4031b2f67ded857fec?placeholderIfAbsent=true&apiKey=4954cd0778ee41b3916c35610d52c9fb"
            className={styles.icon}
            alt=""
          /> */}

          export const LoginPage = () => {
            const [isSignupModalVisible, setSignupModalVisible] = useState(false); // Added state for signup modal visibility

            // Toggle signup modal visibility
            const toggleSignupModal = () => {
              setSignupModalVisible(prev => !prev);
            };

            return (
              <>
                <form className={styles.loginContainer}>
                  <div className={styles.content}>
                    <div className={styles.header}>
                      <h1 className={styles.title}>My ACCOUNT</h1>
                    </div>

                    <Input label="Email" type="email" id="email" />
                    <Input label="Password" type="password" id="password" />

                    <Button variant="primary">LOGIN</Button>

                    <div className={styles.links}>
                      <a href="#" className={styles.storeLink}>
                        RETURN TO STORE
                      </a>
                      <a href="#" className={styles.forgotLink}>
                        Forgot Password
                      </a>
                    </div>

                    <div className={styles.signupSection}>
                      <span className={styles.signupText}>Sign Up</span>
                      <Button variant="secondary" onClick={toggleSignupModal}>SIGN UP NOW !</Button> {/* Open the modal */}
                    </div>
                  </div>
                </form>

                {/* Show the Signup Modal when isSignupModalVisible is true */}
                {isSignupModalVisible && (
                  <div className={styles.modalOverlay} onClick={toggleSignupModal}>
                    <div className={styles.modalContainer} onClick={(e) => e.stopPropagation()}>
                      <button className={styles.closeButton} onClick={toggleSignupModal}>
                        âœ–
                      </button>
                      <SignupForm /> {/* Render the Signup Form inside the modal */}
                    </div>
                  </div>
                )}
              </>
            );
          };
