import React, { useState } from "react";
import { SignupForm } from "../signup/SignupForm";  // Corrected path
import { Input } from "./components/Input";
import { Button } from "./components/Button";
import styles from "./LoginPage.module.css";

export const LoginPage = () => {
  const [isSignupModalVisible, setSignupModalVisible] = useState(false);

  const toggleSignupModal = () => {
    setSignupModalVisible((prev) => !prev);
  };

  return (
    <form className={styles.loginContainer}>
      <div className={styles.content}>
        <div className={styles.header}>
          <h1 className={styles.title}>My ACCOUNT</h1>
        </div>

        <Input label="Email" type="email" id="email" />
        <Input label="Password" type="password" id="password" />
        <Button variant="primary">LOGIN</Button>

        <div className={styles.links}>
          <a href="#" className={styles.storeLink}>
            RETURN TO STORE
          </a>
          <a href="#" className={styles.forgotLink}>
            Forgot Password
          </a>
        </div>

        <div className={styles.signupSection}>
          <span className={styles.signupText}>Sign Up</span>
          <Button variant="secondary" onClick={toggleSignupModal}>
            SIGN UP NOW !
          </Button>
        </div>
      </div>

      {/* Show SignUp Modal */}
      {isSignupModalVisible && (
        <div className={styles.modalOverlay} onClick={toggleSignupModal}>
          <div className={styles.modalContainer} onClick={(e) => e.stopPropagation()}>
            <button className={styles.closeButton} onClick={toggleSignupModal}>
              âœ–
            </button>
            <SignupForm />
          </div>
        </div>
      )}
    </form>
  );
};
